settings.custom_css:
selector.scrolled {
    background: #151514;
    -webkit-box-shadow: -3px -3px 7px 4px rgba(0, 0, 0, 0.03);
    box-shadow: -3px -3px 7px 4px rgba(0, 0, 0, 0.03);
    -webkit-transition: 0.2s ease-in-out;
    transition: 0.2s ease-in-out;
}
selector img {
    width: 120px;
    -webkit-transition: .4s ease-in-out;
    transition: .4s ease-in-out;
}
  @media (min-width: 768px) {
    selector img {
      width: 160px;
    } 
selector.scrolled img {
    width: 120px !important;
    max-width: 120px !important;
}
}

/* === FORM STYLES (Optimized for Elementor) === */
selector .color-brand {
  color: #c81510;
}

selector p.form-intro {
  text-align: left;
  line-height: 1.5;
  font-family: "Inter", sans-serif;
  font-size: 19px;
}

/* Question labels */
selector .infusion-field.mt-0 p {
  font-weight: 600;
  text-align: left;
  line-height: 1.5;
  font-size: 19px;
  margin: 0 0 10px 0;
}

/* Form wrapper */
selector form.wpcf7-form {
  width: 100% !important;
}

/* Inputs + textareas */
selector form.wpcf7-form input,
selector form.wpcf7-form textarea {
  margin-top: 10px;
  margin-bottom: 0;
  font-size: 1rem !important;
  line-height: 1.4 !important;
  font-family: "Inter", sans-serif;
}

selector form.wpcf7-form input[type="text"],
selector form.wpcf7-form input[type="email"],
selector form.wpcf7-form input[type="tel"],
selector form.wpcf7-form textarea {
  display: block;
  width: 100%;
  border: none;
  border-radius: 5px;
  padding: 20px 20px !important;
  padding-left: 55px !important;
  background-color: #f4f1f1;
  box-shadow: inset 0px 7px 16px rgba(0, 0, 0, 0.13);
  transition: 0.4s;
}

selector form.wpcf7-form input::placeholder,
selector form.wpcf7-form textarea::placeholder {
  font-size: 1.1rem;
  font-weight: 400;
}

selector form.wpcf7-form textarea {
  height: 120px;
  resize: vertical;
}

/* ICON positioning */
selector form.wpcf7-form .infusion-field {
  position: relative;
  margin-bottom: 15px;
}

selector form.wpcf7-form .infusion-field i {
  position: absolute;
  top: 22px;
  left: 20px;
  font-size: 1.4rem !important;
  line-height: 1;
  color: #c81510;
}

selector form.wpcf7-form .infusion-field textarea ~ i {
  bottom: 85px;
  top: auto;
}

/* Custom Radios */
selector .infusion-field input[type="radio"] {
  display: none;
}

selector .infusion-field .wpcf7-list-item-label {
  position: relative;
  cursor: pointer;
  padding-left: 28px;
  line-height: 1.4;
  font-weight: 500;
}

selector .infusion-field .wpcf7-list-item-label::before {
  content: "";
  position: absolute;
  top: 2px;
  left: 0;
  width: 18px;
  height: 18px;
  border: 2px solid #c81510;
  border-radius: 50%;
  box-sizing: border-box;
  transition: all 0.2s ease;
}

selector .infusion-field input[type="radio"]:checked + .wpcf7-list-item-label::after {
  content: "";
  position: absolute;
  top: 7px;
  left: 5px;
  width: 10px;
  height: 10px;
  background-color: #c81510;
  border-radius: 50%;
}

/* Submit button */
selector form.wpcf7-form input[type="submit"],
selector form.wpcf7-form button[type="submit"] {
  display: block;
  width: 100%;
  margin: 0 auto 12px;
  border: 0;
  border-radius: 50px;
  padding: 25px 10px;
  font-size: 1.4rem !important;
  font-weight: 700;
  text-transform: uppercase;
  background: #c81510;
  color: #fff !important;
  transition: background-color 0.4s ease-in-out;
  cursor: pointer;
}

selector form.wpcf7-form input[type="submit"]:hover,
selector form.wpcf7-form button[type="submit"]:hover {
  background: rgba(200, 21, 16, 0.85);
  color: #fff !important;
}
